styles:
table {
  margin-top: 24px;
}

table, th, td {
  border: 1px solid black;
}

th, td {
  padding: 6px !important;
}

.code-example {
  margin-top: 12px;
  margin-bottom: 24px;
}

.code-example p {
  margin-bottom: 12px;
}

